document.addEventListener('DOMContentLoaded', function() {
    // Получаем элементы
    const groupInput = document.getElementById('education');
    const courseSelect = document.getElementById('course');
    const subjectSelect = document.getElementById('subject');
    const disciplineSelect = document.getElementById('discipline');
    const groupDatalist = document.getElementById('group-list');
    const searchButton = document.querySelector('.upload_item-but-but-search');
    const materialsContainer = document.querySelector('.get_subjects_material');
    const mainMaterialsSection = document.querySelector('.main_download_materials');
    const pracImg = `<svg width="176" height="176" viewBox="0 0 176 176" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
        <rect width="176" height="176" fill="url(#pattern0_211_541)"/>
        <defs>
        <pattern id="pattern0_211_541" patternContentUnits="objectBoundingBox" width="1" height="1">
        <use xlink:href="#image0_211_541" transform="scale(0.00195312)"/>
        </pattern>
        <image id="image0_211_541" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
        </defs>
        </svg>
        `;

    const lecImg = `<svg width="176" height="176" viewBox="0 0 176 176" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
        <rect width="176" height="176" fill="url(#pattern0_211_532)"/>
        <defs>
        <pattern id="pattern0_211_532" patternContentUnits="objectBoundingBox" width="1" height="1">
        <use xlink:href="#image0_211_532" transform="scale(0.00195312)"/>
        </pattern>
        <image id="image0_211_532" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
        </defs>
        </svg>
        `;
        dowImg =`<svg width="68" height="68" viewBox="0 0 68 68" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink">
                                <rect width="68" height="68" fill="url(#pattern0_211_509)"/>
                                <defs>
                                <pattern id="pattern0_211_509" patternContentUnits="objectBoundingBox" width="1" height="1">
                                <use xlink:href="#image0_211_509" transform="scale(0.00195312)"/>
                                </pattern>
                                <image id="image0_211_509" width="512" height="512" preserveAspectRatio="none" xlink:href="data:image/png;base64,"/>
                                </defs>
                            </svg>`;
    // Проверяем наличие элементов
    if (!groupInput || !courseSelect || !subjectSelect || !disciplineSelect || !groupDatalist || !searchButton) {
        console.error('Один из элементов не найден!');
        return;
    }
    
    let debounceTimer;
    let groupsLoaded = false;
    
    // Изначально скрываем подсказки
    groupInput.removeAttribute('list');

    function resetDependentFields(resetFrom) {
        switch(resetFrom) {
            case 'group':
                // Сбрасываем курс, предмет и тип занятия
                resetSelect(courseSelect);
                resetSelect(subjectSelect);
                resetSelect(disciplineSelect);
                subjectSelect.disabled = true;
                break;
                
            case 'course':
                // Сбрасываем предмет и тип занятия
                resetSelect(subjectSelect);
                resetSelect(disciplineSelect);
                subjectSelect.disabled = true;
                break;
                
            case 'subject':
                // Сбрасываем только тип занятия
                resetSelect(disciplineSelect);
                break;
        }
    }
     // Функция сброса селекта в начальное состояние
    function resetSelect(selectElement) {
        const placeholder = Array.from(selectElement.options).find(
            option => option.disabled && option.value === ""
        );
        
        if (placeholder) {
            placeholder.selected = true;
        }
    }
    // Функция загрузки списка групп
    function loadGroups() {
        if (groupsLoaded) return;
        
        fetch('/components/downloadComponents/selectComp.php?action=get_groups')
            .then(response => {
                if (!response.ok) throw new Error('Ошибка сети: ' + response.status);
                return response.json();
            })
            .then(data => {
                if (data.error) {
                    console.error('Ошибка сервера:', data.error);
                    return;
                }
                
                // Очищаем datalist
                groupDatalist.innerHTML = '';
                
                // Добавляем новые группы
                if (data.groups && data.groups.length > 0) {
                    data.groups.forEach(group => {
                        const option = document.createElement('option');
                        option.value = group;
                        groupDatalist.appendChild(option);
                    });
                    
                    groupsLoaded = true;
                    
                    // Показываем подсказки только если есть введенные символы
                    if (groupInput.value.trim().length > 0) {
                        groupInput.setAttribute('list', 'group-list');
                    }
                }
            })
            .catch(error => {
                console.error('Ошибка при загрузке групп:', error);
            });
    }
    
    // Функция загрузки количества курсов для группы
    function loadCourseCount(group) {
        if (!group || group.trim().length === 0) return;
        
        const apiUrl = `/components/downloadComponents/selectComp.php?action=get_course_count&group=${encodeURIComponent(group)}`;
        
        fetch(apiUrl)
            .then(response => {
                if (!response.ok) throw new Error('Ошибка сети: ' + response.status);
                return response.json();
            })
            .then(data => {
                if (data.error) {
                    // По умолчанию 5 курсов при ошибке
                    updateCourseOptions(5);
                } else {
                    updateCourseOptions(data.count);
                }
                
                // После обновления курсов загружаем дисциплины
                if (groupInput.value.trim().length >= 1) {
                    loadSubjects();
                }
            })
            .catch(error => {
                console.error('Ошибка при загрузке количества курсов:', error);
                updateCourseOptions(5); // Значение по умолчанию
            });
    }
    
    // Функция для обновления списка курсов
    function updateCourseOptions(maxCourses) {
        // Сохраняем текущее значение
        const currentValue = courseSelect.value;
        
        // Сохраняем плейсхолдер (первый option)
        const placeholder = courseSelect.options[0];
        
        // Очищаем список
        courseSelect.innerHTML = '';
        
        // Восстанавливаем плейсхолдер
        courseSelect.appendChild(placeholder);
        
        // Добавляем новые опции с форматированным текстом
        for (let i = 1; i <= maxCourses; i++) {
            const option = document.createElement('option');
            option.value = i; // Значение для отправки на сервер
            option.textContent = `${i} курс`; // Отображаемый текст
            courseSelect.appendChild(option);
        }
        
        // Восстанавливаем выбранное значение
        if (currentValue && parseInt(currentValue) <= maxCourses) {
            courseSelect.value = currentValue;
        } else {
            courseSelect.value = '';
        }
    }
    
    // Функция загрузки дисциплин
    function loadSubjects() {
        const group = groupInput.value.trim();
        const course = courseSelect.value;
        
        // Проверяем минимальную длину группы
        if (group.length < 1) {
            subjectSelect.innerHTML = '<option value="" disabled selected hidden>Введите название группы</option>';
            subjectSelect.disabled = true;
            return;
        }
        
        // Проверяем выбран ли курс
        if (!course) {
            subjectSelect.innerHTML = '<option value="" disabled selected hidden>Выберите курс</option>';
            subjectSelect.disabled = true;
            return;
        }
        
        // Показываем статус загрузки
        subjectSelect.innerHTML = '<option value="">Загрузка дисциплин...</option>';
        subjectSelect.disabled = true;
        
        // Формируем URL запроса
        const apiUrl = `/components/downloadComponents/selectComp.php?action=get_disciplines&group=${encodeURIComponent(group)}&course=${encodeURIComponent(course)}`;
        
        fetch(apiUrl)
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                // Очищаем список дисциплин
                subjectSelect.innerHTML = '';
                
                if (data.error) {
                    // Обработка ошибки от сервера
                    const option = document.createElement('option');
                    option.textContent = `Ошибка: ${data.error}`;
                    subjectSelect.appendChild(option);
                    subjectSelect.disabled = true;
                    return;
                } 
                
                if (data.disciplines && data.disciplines.length > 0) {
                    // Добавляем плейсхолдер
                    const defaultOption = document.createElement('option');
                    defaultOption.value = '';
                    defaultOption.disabled = true;
                    defaultOption.selected = true;
                    defaultOption.hidden = true;
                    defaultOption.textContent = 'Выберите предмет...';
                    subjectSelect.appendChild(defaultOption);
                    
                    // Добавляем дисциплины
                    data.disciplines.forEach(discipline => {
                        const option = document.createElement('option');
                        option.value = discipline;
                        option.textContent = discipline;
                        subjectSelect.appendChild(option);
                    });
                    
                    subjectSelect.disabled = false;
                } else {
                    // Нет дисциплин
                    const option = document.createElement('option');
                    option.textContent = 'Дисциплины не найдены';
                    subjectSelect.appendChild(option);
                    subjectSelect.disabled = true;
                }
            })
            .catch(error => {
                console.error('Ошибка при загрузке дисциплин:', error);
                
                // Создаем понятное сообщение об ошибке
                const option = document.createElement('option');
                option.textContent = 'Ошибка загрузки. Проверьте подключение.';
                subjectSelect.innerHTML = '';
                subjectSelect.appendChild(option);
                subjectSelect.disabled = true;
            });
    }
    
    // Функция для управления видимостью подсказок
    function updateDatalistVisibility() {
        if (groupInput.value.trim().length > 0) {
            groupInput.setAttribute('list', 'group-list');
        } else {
            groupInput.removeAttribute('list');
        }
    }
    
    // Обработчики событий
    
    // При вводе в поле группы
    groupInput.addEventListener('input', function() {
        const inputValue = groupInput.value.trim();
        updateDatalistVisibility();
        clearTimeout(debounceTimer);

        if (inputValue.length >= 1) {
            if (!groupsLoaded) loadGroups();
            loadCourseCount(inputValue);
        } else {
            // Сбрасываем все зависимые поля при очистке группы
            resetDependentFields('group');
        }
    });
    
    // При изменении значения группы (например, выбор из подсказки)
    groupInput.addEventListener('change', function() {
        // Загружаем количество курсов для выбранной группы
        if (groupInput.value.trim().length >= 1) {
            loadCourseCount(groupInput.value.trim());
        }
        
        // Добавляем небольшую задержку для гарантии обработки выбора
        setTimeout(loadSubjects, 100);
    });
    
    // При изменении курса
     courseSelect.addEventListener('change', function() {
        // Сбрасываем предмет и тип при изменении курса
        resetDependentFields('course');
        loadSubjects();
    });

     // Обработчик изменения предмета
    subjectSelect.addEventListener('change', function() {
        // Сбрасываем тип занятия при изменении предмета
        resetDependentFields('subject');
    });
    
    // При фокусе на поле группы
    groupInput.addEventListener('focus', function() {
        // Обновляем видимость подсказок
        updateDatalistVisibility();
    });
    
    // При потере фокуса
    groupInput.addEventListener('blur', function() {
        // Добавляем небольшую задержку перед скрытием
        setTimeout(() => {
            if (groupInput.value.trim().length === 0) {
                groupInput.removeAttribute('list');
            }
        }, 200);
    });
    
    // Инициализация при загрузке страницы
    updateDatalistVisibility(); // Синхронизируем начальное состояние
    
    // Инициализируем список курсов с максимальным значением 5
    updateCourseOptions(5);
    
    // Проверяем, есть ли уже значения в полях
    if (groupInput.value && groupInput.value.trim().length >= 1 && courseSelect.value) {
        // Если есть, загружаем дисциплины
        loadSubjects();
    }
    
    // Функция для показа toast-уведомлений
        function showToast(msg, isSuccess = true) {
            const toast = document.getElementById('toast');
            toast.textContent = msg;
            toast.className = 'toast'; // Сбрасываем классы
            toast.classList.add(isSuccess ? 'success' : 'error');
            toast.classList.remove('hidden');
            toast.classList.add('visible'); // Используем visible вместо show
            
            setTimeout(() => {
                toast.classList.remove('visible');
                setTimeout(() => toast.classList.add('hidden'), 300);
            }, 4000);
        }
    // ================== ОБРАБОТЧИК КНОПКИ ПОИСКА ==================
    searchButton.addEventListener('click', function() {
    // Собираем данные из формы
    const formData = new FormData();
    formData.append('group', groupInput.value.trim());
    formData.append('course', courseSelect.value);
    formData.append('subject', subjectSelect.value);
    formData.append('discipline', disciplineSelect.value);

    // Проверка на заполненность полей
    if (!formData.get('group') || !formData.get('course') || !formData.get('subject') || !formData.get('discipline')) {
        showToast('Заполните поля', false);
        return;
    }

    // Отправляем AJAX запрос
    fetch('/components/downloadComponents/dispList.php', {
        method: 'POST',
        body: formData
    })
    .then(response => {
        if (!response.ok) {
            throw new Error('Ошибка сети: ' + response.status);
        }
        return response.json();
    })
        .then(data => {
            // Проверка успешности выполнения запроса
            if (!data.success) {
                throw new Error(data.message || 'Произошла ошибка при поиске материалов');
            }

            // Очищаем контейнер перед добавлением новых материалов
            materialsContainer.innerHTML = '';
            
            // Обновляем ссылку для скачивания Excel файла
            const downloadLink = document.querySelector('.excell_a-downloadST');
            if (downloadLink && data.excell_path) {
                downloadLink.href = data.excell_path;
            }

            // Обработка материалов
            if (data.materials.length > 0) {
                // Показываем секцию с материалами
                mainMaterialsSection.hidden = false;
                
                // Создаем элементы для каждого материала
                data.materials.forEach(material => { 
                    const materialElement = document.createElement('div');
                    materialElement.className = 'get_subjects-item';
                    materialElement.innerHTML = `
                        <div class="get_subjects-img">
                            ${material.type_file_discipline === "Лекция" ? lecImg : ""}
                            ${material.type_file_discipline === "Практика" ? pracImg : ""}
                        </div>
                        <div class="get_subjects-title">
                            <p class="get_subjects-title-text">${material.file_discipline_name}</p>
                        </div>
                        <div class="get_subjects_download">
                            <div class="get_subjects_download-text">
                                скачать
                            </div>
                            <a href="${material.file_path}" download class="get_subjects_download-link">
                                <div class="get_subjects_download-img">
                                    ${dowImg}
                                </div>
                            </a>
                        </div>
                    `;
                    
                    materialsContainer.appendChild(materialElement);
                });
                showToast(`Файлы найдены:`, true);
            } else {
                  showToast('Материалы не найдены', false);
                mainMaterialsSection.hidden = true;
            }
        })
        .catch(error => {
            showToast(error.message, false);
        });
    });
});
























